import React,{useState,useEffect} from 'react'
import CommonSection from '../shared/CommonSection';

import '../styles/tours.css'
import TourCard from './../shared/TourCard'
import tourData from '../assets/data/tours'
import SearchBar from './../shared/SearchBar'
import Newsletter from './../shared/Newsletter'
import { Container, Row, Col } from 'reactstrap'

import useFetch from '../hooks/useFetch'
import { BASE_URL } from '../utils/config';

const Tours = () => {
  const [pageCount,setPageCount] = useState(0);
  const [page,setPage] = useState(0)

  const {data: tours, loading, error} = useFetch(`${BASE_URL}/tours`)
  const {data: tourCount} = useFetch(`${BASE_URL}/tours/serach/getTourCount`)

  useEffect(()=>{
    const pages = Math.ceil(tourCount/4) //for getting 4 tour in 1 page


    setPageCount(pages)
  },[page, tourCount]);

  return (
    <>
      <CommonSection title={"All Tours"} />
      <section>
        <Container>
          <Row>
            <SearchBar />
          </Row>
        </Container>
      </section>
      <section>
        <Container>
          {loading }
         {
           !loading && !error &&  <Row>
           {
             tours?.map(tour => (
             <Col lg='3' className='mb-4' key={tour.id} >
               <TourCard tour={tour} />
             </Col>))
           }
           <Col lg="12">
             <div className="pagination d-flex align-items-center 
             justify-content-center gap-4 mt-3">
               {[...Array(pageCount).keys()].map(number=>(
                 <span key={number} onClick={()=>setPage(number)}
                 className={page===number ? "active__page":""}
                 >
                   {number+1}
                 </span>
               ))}
             </div>
           </Col>
         </Row>
         }
        </Container>
      </section>
      <Newsletter/>
    </>
  )
}

export default Tours
